mapper.select:
select (select tc.code_content from tb_code tc where tc.code_id = tp.solution_id) as "solution", (select count(*) from tb_point tp2 where tp2.post_type = 'Q' and tp2.act_code like 'PA01%' and tp2.solution_id = tp.solution_id and tp2.display_yn = 'Y'
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "question", (select count(*) from tb_point tp2 where tp2.post_type = 'A' and tp2.act_code like 'PA03%' and tp2.solution_id = tp.solution_id and tp2.display_yn = 'Y'
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "answer", (select count(*) from tb_question tq2 where tq2.solution_id = tp.solution_id and tq2.display_yn = 'Y' and tq2.score < 3
and tq2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "notSuccess", (select count(*) from tb_point tp2 where tp2.act_code like 'PA02%' and tp2.solution_id = tp.solution_id
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "scoreCount", to_char(avg(tb_qa.score), 'FM9.0') as "avgScore", concat(to_char(AVG(tb_qa.tadate - tb_qa.tqdate), 'DD'), ' ì¼') as "dueDate", (select sum(tp3.point_value ) from tb_point tp3 where tp3.solution_id = tp.solution_id and tp3.display_yn = 'Y'
and tp3.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "point" from tb_point tp left join (select ta.reg_date as "tadate", tq.reg_date as "tqdate", tq.score as "score", tq.solution_id as "solution" from tb_question tq left join tb_answer ta on tq.answer_id = ta.answer_id where 1=1 and tq.display_yn = 'Y'
and tq.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as TB_QA on TB_QA.SOLUTION = TP.solution_id where 1=1
and tp.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
group by tp.solution_id

select (select tu.user_name from tb_user tu where tu.user_id = tp.user_id ) as "userName", (select tu.dept from tb_user tu where tu.user_id = tp.user_id ) as "userDept", (select tu.user_id from tb_user tu where tu.user_id = tp.user_id ) as "user_id", (select count(*) from tb_point tp2 where tp2.post_type = 'Q' and tp2.act_code like 'PA01%' and tp2.display_yn = 'Y'
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
and tp2.user_id = tp.user_id ) as "question", (select count(*) from tb_point tp2 where tp2.post_type = 'A' and tp2.act_code like 'PA03%' and tp2.display_yn = 'Y'
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
and tp2.user_id = tp.user_id ) as "answer", (select count(*) from tb_point tp2 where tp2.act_code like 'PA02%' and TP2.display_yn = 'Y' and tp2.user_id = tp.user_id
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "scoreCount", to_CHAR(avg(tb_up.p_score), 'FM9.0') as "avgScore", (select SUM(tp2.point_value) from tb_point tp2 where tp2.user_id = tp.user_id and tp2.display_yn = 'Y'
and tp2.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
) as "point" from tb_point tp left join (select ta.reg_userid as userid, foo.s as p_score from tb_answer ta inner join (select tq.answer_id as a , tq.score as s from tb_question tq where tq.score is not null and tq.display_yn = 'Y'
and tq.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
)as foo on foo.a = ta.answer_id where ta.display_yn = 'Y' ) as tb_up on tb_up.userid = tp.user_id where 1=1
and tp.reg_date between to_timestamp(concat(#{sDate},' 00:00:00'), 'YYYY-MM-DD HH24:MI:SS.MS') and to_timestamp(concat(#{eDate},' 23:59:59'), 'YYYY-MM-DD HH24:MI:SS.MS')
group by tp.user_id order by tp.user_id;
