mapper.select:
select /* sdk knm/KAKNM01_SQL.xml - srchList */ (ROW_NUMBER() OVER()) AS rowNum , foo.solution_id , foo.title , foo.reg_userid_tq , foo.reg_date_tq , foo.reg_userid_ta , foo.reg_date_ta , foo.term , foo.status from tb_tag tt inner join (select (select tc.code_content from tb_code tc where tc.code_id = tq.solution_id ) as solution_id , tq.title as title , (select tu.user_name from tb_user tu where tu.user_id = tq.reg_userid ) as reg_userid_tq , tq.reg_date as reg_date_tq , (select tu.user_name from tb_user tu where tu.user_id = ta.reg_userid ) as reg_userid_ta , ta.reg_date as reg_date_ta , (case when TO_CHAR((ta.reg_date - tq.reg_date ), 'DD') > '00' then CONCAT(TO_CHAR((ta.reg_date - tq.reg_date ), 'DD'), ' DAYS') when TO_CHAR((ta.reg_date - tq.reg_date ), 'DD') <= '00' then CONCAT(TO_CHAR((ta.reg_date - tq.reg_date ), 'HH'), ' HOURS') end)as term , (case when tq.status = 'SS' then '완료' when tq.status = 'RQ' then '재질문' when tq.status = 'NN' then '미완료' end) as status , tq.question_id from tb_question tq left join tb_answer ta on tq.answer_id = ta.answer_id where 1=1
and tq.title like '%'||#{title}||'%'
and tq.status = #{status}
ORDER BY tq.reg_userid
ORDER BY ta.reg_userid
ORDER BY term
ORDER BY tq.status
ORDER BY tq.reg_date desc
) as foo on tt.post_id = foo.question_id WHERE 1=1
AND tt.tag_type = 'TAG' AND tt.tag_value = #{tag_tag}
AND tt.tag_type = 'ERC' AND tt.tag_value = #{tag_erc}
AND tt.tag_type = 'ERT' AND tt.tag_value = #{tag_ert}


SELECT /* sdk knm/KAKNM01_SQL.xml - srchPrList */
       project_id
     , project_name
     , customer
     , CONCAT(project_start_date, ' ~ ', project_end_date) as term
  FROM tb_project
 WHERE 1=1 



   AND project_id = #{project_id}




   AND project_name = #{project_name}




   AND customer = #{customer}

		 /* sdk knm/KAKNM01_SQL.xml - srchQuId */
            SELECT max(#{question_id}) 
              FROM tb_question
