mapper.insert:

UPDATE tb_user
SET login_cnt = login_cnt + 1
WHERE user_id = #{user_id}

mapper.select:

SELECT login_cnt
FROM tb_user
WHERE user_id = #{user_id}

mapper.update:

UPDATE tb_user
SET login_lock='Y'
WHERE user_id= #{user_id}

mapper.select:

SELECT login_lock
FROM tb_user
WHERE user_id = #{user_id}

mapper.update:

UPDATE tb_user
SET user_pw = #{user_pw}
WHERE user_id = #{user_id}

mapper.select:

SELECT COUNT(user_id)
FROM tb_user
WHERE user_id = #{user_id}
